        for int i = 0; i < k; ++i) rm[A[i]] = 1; 
        int N = s.size(), M = p.size(), j = 0; 
        for (int i = 0; i < N && j < M; ++i) {
            if (rm[i] == 1) continue; 
            if (s[i] == p[j]) ++j; 
        }
        return j == M; 
