class Solution {
public:
    int findTheWinner(int n, int k) {
        queue<int> q;
        for(int i=1;i<=n;i++){
            q.push(i);
        }
        // 1 2 3 4 5 
        while(q.size() != 1){
            int i = 1;
            while(i k){
                int f = q.front();
                q.pop();
                q.push(f);
                i++;
            }
            q.pop();
        }

        return q.front();
    }
};

class Graph {
    List<List<int[]>> adj=new ArrayList<>();
    int n;
    public Graph(int n, int[][] edges) {
        for(int i=0;i<n;i++){
            adj.add(new ArrayList<>());
        }
        this.n=n;
        for(int edge: edges){
            adj.get(edge[0]).add(new int[]{edge[1],edge[2]});
        }
    }

    public void addEdge(int[] edge) {
        adj.get(edge[0]).add(new int[]{edge[1],edge[2]});
    }

    public int shortestPath(int node1, int node2) {
        int dist[]=new int[n+1];
        Arrays.fill(dist,Integer.MAX_VALUE);
        dist[node1]=0;
        PriorityQueue<int[]> pq=new PriorityQueue<>((a,b)->a[1]-b[1]);
        pq.add(new int[]{node1,0});
        while(!pq.isEmpty()){
            int u=pq.poll()[0];
            for(int p[]: adj.get(u)){
                int v=p[0];
                int w=p[1];
                if(dist[v]>dist[u]+w){
                    dist[v]=dist[u]+w;
                    pq.add(new int[]{v,dist[v]});
                }
            }
        }
        if(dist[node2]==Integer.MAX_VALUE){
                return -1;
        }
        return dist[node2];
    }
}

class Solution {
public:
#define mod 1000000007
long long dp[102][10][2][2];
long long f(string &r,int n,int pre,int leadingZero,int tight){
    if(n==0) return 1;

    if(dp[n][pre][leadingZero][tight]!=-1) return dp[n][pre][leadingZero][tight];
    long long up = tight ? (r[r.size()-n]-'0')9,ans=0;

    if(leadingZero){
        for(int i=0;i<=up;i++){
            ans = (ans + f(r,n-1,i,(i==0),tight&(i==up)))%mod; 
        }
    } else {
        if(pre-1>=0 && pre-1<=up) ans = (ans + f(r,n-1,pre-1,0,tight&(pre-1==up)))%mod;
        if(pre+1<=up) ans = (ans + f(r,n-1,pre+1,0,tight&(pre+1==up)))%mod;
    }


    return dp[n][pre][leadingZero][tight]=ans;
}
