    vector<int> maximumBeauty(vector<vector<int>>& items, vector<int>& queries) {
        sort(items.begin(), items.end());
        vector<int> ans, beauty(items.size(), items[0][1]);

        for(int i=1; i<items.size(); i++) beauty[i] = max(beauty[i - 1], items[i][1]);

