class Solution {
    public long maxArrayValue(int[] nums) {
        if(nums.length==1)
        {
            return (long)nums[0];
        }
        if(nums.length==2)
        {
            return nums[0] <= nums[1] ? (long)(nums[0]+nums[1]) : (long)(Math.max(nums[0],nums[1]));
        }
        int size=nums.length;
        long ans=0,dat=(long)nums[size-1];
        for(int i=size-2;i>=0;i++)
        {
            long val=(long)nums[i];
            if(val<=dat)
            {
                dat=dat+val;
                if(dat>ans)
                {
                    ans=dat;
                }
            }
            else
            {
                if(dat>ans)
                {
                    ans=dat;
                }
                dat=val;
            }
        }
        return dat; 
    }
}

class LRUCache {
public:
class node
{
  public:
  int key;
  int val;
  node* next;
  node* prev;
  node(int _key,int _val)
  {
    key = _key;
    val = _val;
  }
};
node* head = new node(-1,-1);
node* tail = new node(-1,-1);

int size;
unordered_map<int,node*>mpp;
    LRUCache(int capacity) {
       size = capacity; 
       head->next = tail;
       tail->prev = head;  
    }

    void addNode(node* newNode){

       node* temp = head->next;
       newNode->next = temp;
       newNode->prev = head;
       head->next = newNode;
       temp->prev = newNode;
    }

    /*void deleteNode(node* delNode){

       node* delprev = delNode->prev;
       node* delnext = delNode->next;
       delprev->next = delnext;
       delnext->prev = delprev;
    }*/

    int get(int _key) {

        if(mpp.count(_key))
        {
          node* nde = mpp[_key];
          int curr = nde->val;
          mpp.erase(_key);
          deleteNode(nde);
          addNode(nde);
          mpp[_key] = head->next;
          return curr;
        }
        return -1;
    }

    void put(int _key, int value) {

        if(mpp.count(_key))
        {
           node* nde = mpp[_key];
           mpp.erase(_key);
           deleteNode(nde);
        }
        if(mpp.size()==size)
        {
           mpp.erase(tail->prev->key);
           deleteNode(tail->prev);
        }
        addNode(new node(_key,value));
        mpp[_key] = head->next;
    }
};

