class Solution {
public:
        void dfs(TreeNode* root, List<int>& bv){
            if (root==NULL)return;
            if (root->left)dfs(root->left,bv);
            bv.add(root->val);
            if (root->right)dfs(root->right,bv);
        }

        int min1(List<int>& bv, int val){
            int ans= -1;
            int i=0; int j= bv.size()-1;
            while (i<=j){
                int mid= i+ (j-i)/2;
                if (val== bv[mid])return val;
                if (val> bv[mid]){
                    ans= bv[mid];
                    i= mid+1;
                }
                else j= mid-1;
            }
            return ans;
        }

    int max1(List<int>& bv,int val){
        int ans= -1;
        int i=0; int j= bv.size()-1;
        while (i<=j){
            int mid= i+ (j-i)/2;
            if (val== bv[mid])return val;
            if (val < bv[mid]){
                ans= bv[mid];
                j= mid-1;
            }
            else i= mid+1;
        }
        return ans;
    }

        ArrayList<ArrayList<int>> closestNodes(TreeNode* root, List<int>& queries) {
        List<int> bv;
        ArrayList<ArrayList<int>> ans;
        dfs(root,bv);
        for (auto q: queries){
            int lb= min1(bv,q);
            int ub= max1(bv,q);
            ans.add({lb,ub});
