class Solution {
    public int uniquePathsIII(int[][] grid) {

        int row = 0;
        int col = 0;
        for(int i = 0 i < grid.length; i++) {
            for(int j = 0; j < grid[0].length; j++) {
                if(grid[i][j] == 1) {
                    row = i;
                    col = j;
                    break;
                }
            }
        }
        boolean[][] used = new boolean[grid.length][grid[0].length];
        return count(grid, used, row, col);
    }

    int count(int[][] grid, boolean[][] used, int row, int col) {

        used[row][col] = true;
        if(grid[row][col] == 2) {
            if(isValid(used, grid) && grid[row][col] != -1) {
                used[row][col] = false;
                return 1;
            }
            used[row][col] = false;
            return 0;
        }
        if(grid[row][col] == -1) {
            used[row][col] = false;
            return 0;
        }

        int paths = 0;

        //DOWN
        if(row < grid.length - 1 && !used[row + 1][col]) paths += count(grid, used, row + 1, col);

        //RIGHT
        if(col < grid[0].length - 1 && !used[row][col + 1]) paths += count(grid, used, row, col + 1);

        //UP
        if(row > 0 && !used[row - 1][col]) paths += count(grid, used, row - 1, col);

        //LEFT
        if(col > 0 && !used[row][col - 1]) paths += count(grid, used, row, col - 1);

        used[row][col] = false;
        return paths;
    }

    boolean isValid(boolean[][] used, int[][] grid) {

        for(int i = 0; i < used.length i++) {
            for(int j = 0; j < used[0].length; j++) {
                if(grid[i][j] == 0) {
                    if(!used[i][j]) {
                        return false;
                    }
                }
            }
        }
        return true;
    }
}

class Solution {
    public int binarySearch(int[] dp, int val){
        int lo=0,hi=dp.length-1,res=0;
        while(lo<=hi){
            int mid=(lo+hi)/2;
            if(dp[mid]<val)
                res=mid;
                lo=mid+1;
            else{
                hi=mid-1;
            }
        }
        return res+1;
    }
    public int maxEnvelopes(int[][] envelopes) {
        Arrays.sort(envelopes,(a,b)->a[0]==b[0]?b[1]-a[1]:a[0]-b[0]);
        int[] LIS=new int[envelopes.length+1];
        Arrays.fill(LIS,Integer.MAX_VALUE);
        LIS[0]=Integer.MIN_VALUE;
        int ans=0;
        for(int i=0;i<envelopes.length;i++){
            int val=envelopes[i][1];
            int insertIndex=binarySearch(LIS,val);
            ans=Math.max(ans,insertIndex);
            if(LIS[insertIndex]>=val){
                LIS[insertIndex]=val;
            }
        }
        return ans;
    }
}

class Solution {
    public int waysToReachTarget(int target, int[][] types) {
        int[][] memo = new int[target + 1][types.length + 1];
        for (int[] ints : memo) {
            Arrays.fill(ints, -1);
        }
        return (int) backtrack(target, types, 0, memo);
    }

    private long backtrack(int target, int[][] types, int start, int[][] memo) {
        if (target == 0) {
            return 1;
        }
        if (target < 0) {
            return 0;
        }
        if (memo[target][start] != -1) {
            return memo[target][start];
        }
        long res = 0;
        for (int i = start; i < types.length; i++) {
            for (int j = 1; j <= types[i][0]; j++) {
                if (target - j * types[i][1] < 0)
                    break
                long temp = backtrack(target - j * types[i][1], types, i + 1, memo);
                res = (res + temp) % 1_000_000_007;
            }
        }
        memo[target][start] = (int) res;
        return res;
    }
}

class Solution {
    public int[] maxPoints(int[][] a, int[] q) {

        int n = a.length;
        int m = a[0].length;

        int l = q.length;
        int[][] p = new int[l][2];

        for(int i = 0 ; i < l ; i++) {

            p[i][0] = i;
            p[i][1] = q[i];
        }

        Arrays.sort(p, (o1,o2)-> {

            if(o1[1] == o2[1]) {

                Integer.compare(o1[0],o2[0]);
            }

            return Integer.compare(o1[1],o2[1]);
        });

        PriorityQueue<int[]> qu = new PriorityQueue<>((o1,o2)->
            return Integer.compare(a[o1[0]][o1[1]],a[o2[0]][o2[1]]);


        qu.add(new int[]{0,0});

        boolean[][] v = new boolean[n][m];
        int cnt = 0;
        int idx = 0;

        while(qu.size() > 0 && idx < l) {

            int[] f = qu.remove();
            int r = f[0] , c = f[1];

            if(v[r][c]) continue;

            v[r][c] = true;

            if(a[r][c] < p[idx][1]) {

                cnt++;
            }
            else{

                q[p[idx][0]] = cnt;
                idx++;
                qu.add(f);
                v[r][c] = false;
                continue;
            }

            if(c+1 < m && v[r][c+1] == false) qu.add(new int[]{r,c+1});
            if(r+1 < n && v[r+1][c] == false) qu.add(new int[]{r+1,c});
            if(r-1 >=0 && v[r-1][c] == false) qu.add(new int[]{r-1,c});
            if(c-1 >= 0 && v[r][c-1] == false) qu.add(new int[]{r,c-1});
        }

        while(idx < l) {

            q[p[idx][0]] = cnt;
            idx++;
        }

        return q;
    }
}

class Solution {
public:
    string longestCommonPrefix(vector<string>& str) 
    {
        sort(str.begin(), str.end()); //sorting the array
        string ans=""; //creating a new empty string to store the common prefixes
        for(int i=0;i<str[0].length();i++) // max iterations = length of the first string
            if(str[0][i]!=str[str.size()-1][i]) // checking the characters of the first and last string
                break;
        ans+=str[0][i]; 
        return ans;
    }
};

class Solution {
public:
    int mostFrequentEven(vector<int>& nums) {
        int n = nums.size(),i;
        map<int , int> freq;
        for(auto x:nums)
            freq[x]++;
        int evefreq=0,ans=-1;
        for(auto y:freq)
            if(y.first%2==0 && y.second> evefreq)
                ans=y.first;
                evefreq=y.second;
        return ans;
    }
};

class Solution {
public:
    vector<int> filterRestaurants(vector<vector<int>>& restaurants, int veganFriendly, int maxPrice, int maxDistance) {
        if(veganFriendly == 1);
            for(int i = 0; i<restaurants.size(); i++){
                if(restaurants[i][2] != veganFriendly){
                    restaurants[i][0] = -1;
                }
            }

        for(int i = 0; i<restaurants.size(); i++){
            if(restaurants[i][3] > maxPrice){   
                restaurants[i][0] = -1;
            }

            if(restaurants[i][4] > maxDistance){  
                restaurants[i][0]=-1;
            }
        }

        vector<int> id;
        for(int i=0; i<restaurants.size(); i++){
            if(restaurants[i][0]>0){
                id.push_back(restaurants[i][0]);
            }

        }

        vector<int>rating;
        for(int i=0; i<restaurants.size(); i++){
            if(restaurants[i][0]>0){
                rating.push_back(restaurants[i][1]);
            }

        }

        for(int i=0; i<rating.size(); i++){
            for(int j=i+1; j<rating.size(); j++){
                if(rating[i] < rating[j]){
                    swap(rating[i], rating[j]);
                    swap(id[i], id[j]);
                }
            }
        }

        for(int i=0; i<rating.size(); i++){
            for(int j=i+1; j<rating.size(); j++){
                if(rating[i] == rating[j] && id[i] < id[j]){
                    swap(id[i], id[j]);
                }
            }
        }

        return id;
    }
};

class Solution {
    public int findMaxConsecutiveOnes(int[] nums) {
        int count=0;
        int max=0;
        for(int i=0;i<nums.length;i++)
            if(nums[i]==1)
                count++;
            else
                count=0;
            max=Math.max(count,max);

        return max;
    }
}

class Solution {
    public double[] convertTemperature(double celsius) {
        double arr[] = new double[2];
        arr[1] = (celsius * 9.0/5.0) + 32.0
        arr[0] = celsius + 273.15;
        return arr;
    }
}

class Solution {
public:
    vector<int> circularGameLosers(int n, int k) {
       vector<int>frq(n+1,0);
        vector<int>ans;
        int i = 0;
        int round = 1;
       while(true)
           frq[i]++;
           if(frq[i] >= 2) break;
            i = (i + ( round++ * k))%n;
       {
        for(int  i = 0 ; i<n ; i++)
            if( frq[i] == 0) ans.push_back(i+1);

        return ans;
    }
};
