class Solution {
    public boolean isStrictlyPalindromic(int n) {
        boolean ans = false;
        for(int i=2; i<n-2; i++){
            int num = n;
            int reverse=0;
            int rem;
            while(num!=0)
                rem = num%i;
                reverse = (reverse*10)+(rem);
                num=num/i;
            if(reverse==n){
                ans = true;
            }
            else{
                ans=false;
                break;
            }
        }
        return ans;
    }
}

class Solution {
public:
    int longestWPI(vector<int>& hours) {
    map<int,int> mp;
    int k=0,ans=0;
    for(int i=0;i<hours.size();i++)
    {
        if(hours[i]>8)  k++; else   k--;
        if(k>0)
        {
        ans=max(ans,i+1)
        } else{
            if(mp.find(k-1)!=mp.end()){
                ans=max(ans,i-mp[k-1]);
            }
             if(mp.find(k)==mp.end()){
                mp[k]=i;
            }  
        }     
    }    
    return ans;
    }
};

class Solution {
    public int maxProfit(int[] prices) {
        int profit=0
        for(int i=1; i<prices.length; i++){
            if(prices[i]>prices[i-1]){
                profit+=prices[i]-prices[i-1];
            }
        }
        return profit;
    }
}

class Solution {
    boolean isSimilar(String str1,String str2)
    {
        int count=0;
        for(int i=0;i<str1.length();i++)
        {
            if(str1.charAt(i)!=str2.charAt(i)) count++;
                if(count>2)
                {
                    return false;
                }
            }
        }
        return count==0 || count==2;
    }
    void dfs(String curr, String []strs, Set<String> visited)
    {
        if(visited.contains(curr))
        {
            return;
        }
        visited.add(curr);
        for(int i=0;i<strs.length;i++)
        {
         if(isSimilar(curr,strs[i])) dfs(strs[i],strs,visited);
        }
    }
    public int numSimilarGroups(String[] strs)
    {
        int count =0;

        if(strs==null ||strs.length ==0)
        {
            return 0;
        }

        Set<String> visited = new HashSet<>();
        for(String s:strs)
        {
            if(!visited.contains(s)) dfs(s,strs,visited);
                count++;
        }
        return count;
    }
}

class Solution {
public:
    int mostFrequentEven(vector<int>& nums) {
        int n = nums.size(),i;
        map<int , int> freq;
        for(auto x:nums)
            freq[x]++;
        int evefreq=0,ans=-1;
        for(auto y:freq)
            if(y.first%2==0 && y.second> evefreq)
                ans=y.first;
