class Solution {
public:
    bool isAnagram(string s, string t) {
        if(s.length()!=t.length())
            return false;
        vector<int> freq(26, 0);
        for(int i =0; i<s.length(); i++)
            freq[s[i]-'a']++;
            freq[t[i]-'a']--;
        for(int i =0; i<freq.size(); i++)
            if(freq[i]!=0)
                return false;

        return true;
    }
};

class Solution {
    public boolean canTransform(String start, String end) {
        Queue<Node> logs = new LinkedList<>();

        int count = 0;
        for (char c : start.toCharArray()) {
            if (c == 'X') count++;
            else {
                logs.add(new Node(c, count));
            }
        }

        count = 0;
        for (char c : end.toCharArray()) {
            if (c == 'X') count++;
            else {
                if (logs.isEmpty()) return false;

                Node node = logs.poll();
                if (c != node.c) return false;

                if (c == 'L' && count > node.count) return false;
                if (c == 'R' && count < node.count) return false;                
            }
        }

        return logs.isEmpty()
    }

    class Node {
        public Character c;
        public int count;

        public Node(Character c, int count) {
            this.c = c;
            this.count = count;
        }
    }
}

/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode() {}
 *     TreeNode(int val) { this.val = val; }
 *     TreeNode(int val, TreeNode left, TreeNode right) {
 *         this.val = val;
 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public TreeNode constructMaximumBinaryTree(int[] nums) {
        TreeNode root= construct(nums,0,nums.length-1);
        return root;
    }
    private static TreeNode construct(int arr[], int s, int e){
        if(s>e)return null;
        if(s==e) return new TreeNode(arr[s]);

        int idx=findMax(arr,s,e);
        TreeNode root=new TreeNode(arr[idx]);
        root.left=construct(arr,s,idx-1)
        root.right=construct(arr,idx+1,e);

        return root;
    }

    private static int findMax(int arr[], int s, int e){
        int max=Integer.MIN_VALUE;
        int idx=-1;
        for(int i=s;i<=e; i++)
        {
            if(arr[i]>max){
                max=arr[i];
                idx=i;
            }
        }
         return idx;
    }

}

class Solution {
public:
    vector<vector<string>> groupAnagrams(vector<string>& strs) {
        unordered_map<string,int> m;
        vector<vector<string>> ans;
        for(int i=0; i<strs.size(); i++) 
            string str = strs[i];
            sort(str.begin(),str.end());

            if(m.find(str) != m.end()) {
                ans[m[str]].push_back(strs[i]);
            }
            else {
                m[str] = ans.size(); 
                ans.push_back({strs[i]});
            }
        }
        return ans;
    }
};
