class Solution {
public:
    vector<int> circularGameLosers(int n, int k) {
       vector<int>frq(n+1,0);
        vector<int>ans;
        int i = 0;
        int round = 1;
       while(true)
           frq[i]++;
           if(frq[i] >= 2) break;
            i = (i + ( round++ * k))%n;
       {
        for(int  i = 0 ; i<n ; i++)
