        }
    }

}

    int shortestBridge(vector<vector<int>>& grid) {

        vector<pair<int,int>> v;
        vector<pair<int,int>> v1;
        vector<pair<int,int>> v2;
        int n = grid.size();
        vector<vector<int>> vis(n , vector<int>(n , 0));
        bool flag = 0;
        for(int i=0; i<n; i++)
        {
            for(int j=0; j<n; j++)
            {
                if(grid[i][j] == 1 and vis[i][j] == 0)
                {
                    dfs(i , j , v , vis, grid);
                    if(flag == 0)
                    {
                        v1 = v;
                        flag = 1;
                        v.clear();
                    }
                    else
                    {
                        v2 = v;
                    }
                }

            }

        }
        int ans = 1e9;


        for(auto it: v1)
        {
            int row1 = it.first;
            int col1 = it.second;

            for(auto itt: v2)
            {
