class Solution {
    public int removeDuplicates(int[] nums) {
     int   index = 1;
      int  count = 0;
        for(int i = 1;i<nums.length;i++)
            if(nums[i] == nums[i-1]){
                count++;
            }
            else{
                count = 0;
            }

            if(count <= 1){
                nums[index] = nums[i];
                index++;
            }
        return index;
    }
}

class Solution {
    public int busyStudent(int[] startTime, int[] endTime, int queryTime) {
        int n=endTime.length;
        int cnt=0;
        for(int i=0;i<n;i++)
            if(endTime[i]>=queryTime && queryTime>=startTime[i] )
            {
                cnt++;
            }
        return cnt
    }
}

