class Solution {
    public boolean canTransform(String start, String end) {
        Queue<Node> logs = new LinkedList<>();

        int count = 0;
        for (char c : start.toCharArray()) {
            if (c == 'X') count++;
            else {
                logs.add(new Node(c, count));
            }
        }

        count = 0;
        for (char c : end.toCharArray()) {
            if (c == 'X') count++;
            else {
                if (logs.isEmpty()) return false;

                Node node = logs.poll();
                if (c != node.c) return false;

                if (c == 'L' && count > node.count) return false;
                if (c == 'R' && count < node.count) return false;                
            }
        }

        return logs.isEmpty()
    }

    class Node {
        public Character c;
        public int count;

        public Node(Character c, int count) {
            this.c = c;
            this.count = count;
        }
    }
}

class Solution {
    public int uniquePathsIII(int[][] grid) {

        int row = 0;
        int col = 0;
        for(int i = 0 i < grid.length; i++) {
            for(int j = 0; j < grid[0].length; j++) {
                if(grid[i][j] == 1) {
                    row = i;
                    col = j;
                    break;
                }
            }
        }
        boolean[][] used = new boolean[grid.length][grid[0].length];
        return count(grid, used, row, col);
    }

    int count(int[][] grid, boolean[][] used, int row, int col) {

        used[row][col] = true;
        if(grid[row][col] == 2) {
            if(isValid(used, grid) && grid[row][col] != -1) {
                used[row][col] = false;
                return 1;
            }
            used[row][col] = false;
            return 0;
        }
        if(grid[row][col] == -1) {
            used[row][col] = false;
            return 0;
        }

        int paths = 0;

        //DOWN
        if(row < grid.length - 1 && !used[row + 1][col]) paths += count(grid, used, row + 1, col);

        //RIGHT
        if(col < grid[0].length - 1 && !used[row][col + 1]) paths += count(grid, used, row, col + 1);

        //UP
        if(row > 0 && !used[row - 1][col]) paths += count(grid, used, row - 1, col);

        //LEFT
        if(col > 0 && !used[row][col - 1]) paths += count(grid, used, row, col - 1);

        used[row][col] = false;
        return paths;
    }

    boolean isValid(boolean[][] used, int[][] grid) {

        for(int i = 0; i < used.length i++) {
            for(int j = 0; j < used[0].length; j++) {
                if(grid[i][j] == 0) {
                    if(!used[i][j]) {
                        return false;
                    }
                }
            }
        }
        return true;
    }
}
