class Solution {
public:
    void rotate(vector<int>& nums, int k) 
    int n=nums.size();
    vector<int> temp(nums.size());
    for(int i=0;i<n;i++){
        temp[(i+k)%n]=nums[i];
    }
    nums=temp;
};

class Solution {
    int res = 0;
    public int minIncrements(int n, int[] cost) {
        dfs(1, cost) return res;
    }

    // get the max cost of a path of sub tree root at i;
    public int dfs(int i, int[] cost) {
        if (i > cost.length) return 0;
        int left = dfs(i * 2, cost), right = dfs(i * 2 + 1, cost);
        // you need keep two child tree has the same cost of path.
        // best way to tune the root of lower cost path, increase the
        // lower root, by Math.abs(left - right);
        res += Math.abs(left - right);
        return cost[i - 1] + Math.max(left, right);
    }
}

class Solution {
    public int busyStudent(int[] startTime, int[] endTime, int queryTime) {
        int n=endTime.length;
        int cnt=0;
        for(int i=0;i<n;i++)
            if(endTime[i]>=queryTime && queryTime>=startTime[i] )
            {
                cnt++;
            }
        return cnt
    }
}

class Solution {
    public String smallestEquivalentString(String s1, String s2, String baseStr) {

        UFDS dsu = new UFDS(26);
        for(int i = 0; i < s1.length(); i++)
            dsu.union(s1.charAt(i)-97,s2.charAt(i)-97);

        StringBuilder sb = new StringBuilder(); 

        for(int i = 0; i < baseStr.length(); i++)
            int root = dsu.root(baseStr.charAt(i) - 97);
            sb.append((char)(dsu.min[root] + 97));


        return sb.toString();
    }

    class UFDS{

        int[] id;
        int[] size;
        int[] min;

        public UFDS(int N){

            id = new int[N];
            size = new int[N];
            min = new int[N];
            for(int i = 0; i < N; i++){
                id[i] = i;
                size[i] = 1;
                min[i] = i;
            }
        }

        private int root(int i){
            while (i != id[i]){
                id[i] = id[id[i]];
                i = id[i];
            }
            return i;
        }

        public void union(int p, int q){

            int i = root(p);
            int j = root(q);
            if(i == j)return;
            if(size[i] < size[j]){
                id[i] = id[j];
                size[j] += size[i];
                min[j] = Math.min(min[i],min[j]);
            }
            else{
                id[j] = id[i];
                size[i] += size[j];
                min[i] = Math.min(min[i],min[j]);
            }
        }

    }   
}

class Solution {
public:
    vector<int> circularGameLosers(int n, int k) {
       vector<int>frq(n+1,0);
        vector<int>ans;
        int i = 0;
        int round = 1;
       while(true)
           frq[i]++;
           if(frq[i] >= 2) break;
            i = (i + ( round++ * k))%n;
       {
        for(int  i = 0 ; i<n ; i++)
            if( frq[i] == 0) ans.push_back(i+1);

        return ans;
    }
};

class Solution {
    boolean isSimilar(String str1,String str2)
    {
        int count=0;
        for(int i=0;i<str1.length();i++)
        {
            if(str1.charAt(i)!=str2.charAt(i)) count++;
                if(count>2)
                {
                    return false;
                }
            }
        }
        return count==0 || count==2;
    }
    void dfs(String curr, String []strs, Set<String> visited)
    {
        if(visited.contains(curr))
        {
            return;
        }
        visited.add(curr);
        for(int i=0;i<strs.length;i++)
        {
         if(isSimilar(curr,strs[i])) dfs(strs[i],strs,visited);
        }
    }
    public int numSimilarGroups(String[] strs)
    {
        int count =0;

        if(strs==null ||strs.length ==0)
        {
            return 0;
        }

        Set<String> visited = new HashSet<>();
        for(String s:strs)
        {
            if(!visited.contains(s)) dfs(s,strs,visited);
                count++;
        }
        return count;
    }
}
