    }
};

class Solution {
public:
    void merge(int low, int mid, int high, vector<int> &nums) {
        if (low >= high) 
        return;
        int l = low, r = mid + 1, k = 0, size = high - low + 1;
        vector<int> sorted(size, 0);
        while (l <= mid and r <= high)
            sorted[k++] = nums[l] < nums[r] ? nums[l++] nums[r++];
        while (l <= mid) 
            sorted[k++] = nums[l++];
        while (r <= high) 
            sorted[k++] = nums[r++];
        for (k = 0; k < size; k++)
            nums[k + low] = sorted[k];
    }

    void mergeSort(vector<int>& nums, int start, int end){
        if(start < end){
            int mid = start + (end - start) / 2;
            mergeSort(nums, start, mid);
            mergeSort(nums, mid + 1, end);
            merge(start, mid, end, nums);
        }
    }

    vector<int> sortArray(vector<int>& nums) {
        mergeSort(nums, 0, nums.size()-1);
        return nums;
    }
};

class Solution {
    public Node connect(Node node) {
        Map<Integer, List<Node>> map = new HashMap<>();
        goDFS(0, node, map)
        for (int key : map.keySet()) {
            List<Node> list = map.get(key);
            for (int i = 1; i < list.size(); i++) {
                list.get(i - 1).next = list.get(i);
            }
        }
        return node;
    }

    private void goDFS(int lvl, Node node, Map<Integer, List<Node>> map) {
        if (node == null) return;

        List<Node> list = map.computeIfAbsent(lvl, k -> new ArrayList<>());
        list.add(node);
        lvl++;
        goDFS(lvl, node.left, map);
        goDFS(lvl, node.right, map);
    }
}

class Solution {
 int rm[100001] = {};
    bool valid(string &s, string &p, vector<int> &A, int k) {
        memset(rm, 0, sizeof(rm));
        for int i = 0; i < k; ++i) rm[A[i]] = 1; 
        int N = s.size(), M = p.size(), j = 0; 
        for (int i = 0; i < N && j < M; ++i) {
            if (rm[i] == 1) continue; 
            if (s[i] == p[j]) ++j; 
        }
        return j == M; 
    }
public:
    int maximumRemovals(string s, string p, vector<int>& A) {
        int L = 0, R = A.size();
        while (L <= R) {
            int M = (L + R) / 2;
            if (valid(s, p, A, M)) L = M + 1;
            else R = M - 1;
        }
        return R;
    }
};

class Solution {
    public int[] char_map;
    public int countCharacters(String[] words, String chars) {
        char_map=new int[26];
       int ans=0;
       for(char c : chars.toCharArray()){
           char_map[c-'a']++;
       }

       for(String word: words){
           int[] dummyArray = Arrays.copyOf(char_map, char_map.length);
           int count =0
           for(char c: word.toCharArray()){
               if(dummyArray[c-'a']>0){
                   count++;
                   dummyArray[c-'a']--;
               }
           }
           if(count==word.length()){
               ans+=word.length();
           }
       }
       return ans;
    }
}

