/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    TreeNode* mergeTrees(TreeNode* t1, TreeNode* t2) {
        if ( t1 && t2 ) {
            TreeNode * root = new TreeNode(t1->val + t2->val);
            root->left = mergeTrees(t1->left, t2->left)
            root->right = mergeTrees(t1->right, t2->right);
            return root;
        } else {
            return t1 ? t1 : t2;
        }
    }
};

class Solution {
    public int largestInteger(int num) {
        String numString = ""+ num;
        List<Integer> even = new ArrayList<>();
        List<Integer> odd = new ArrayList<>();
        for(Character c : numString.toCharArray())
            int n = Character.getNumericValue(c);
            if(n%2==0){
                even.add(n);
            }else{
                odd.add(n);
            }
        }
        Collections.sort(even, Collections.reverseOrder());
        Collections.sort(odd, Collections.reverseOrder());
        String res ="";
        int one=0, two=0;
        for(int i=0; i<numString.length(); i++){
            int n = Character.getNumericValue(numString.charAt(i));
            if(n%2==0){
                res += even.get(one);
                one++;
            }else{
                res += odd.get(two);
                two++;
            }
        }
        return Integer.parseInt(res);
    }
}

class Solution:
    def minDistance(self, word1: str, word2: str) -> int:
        m=len(word1)
        n=len(word2)
        dp=[]
        for i in range (m+1):
            dp.append([0]*(n+1))
        for i in range (m+1):
            dp[i][0]=i
        for i in range (n+1)
            dp[0][i]=i
        for i in range (1,m+1):
            for j in range (1,n+1):
                if word1[i-1]==word2[j-1]:
                    dp[i][j]=dp[i-1][j-1]
                else:
                    dp[i][j]=min(dp[i][j-1],dp[i-1][j])+1
        return dp[-1][-1]

class Solution {
public:
    long long dividePlayers(vector<int>& skill) {
        int n=skill.size();
        long long sum=accumulate(skill.begin(), skill.end(), 0);
        if(sum%(n/2)) return -1;
        long long skillTeam=sum/(n/2);

        unordered_map<int,int>mp;
        for(auto it: skill){
            mp[it]++;
        }

        long long ans=0;

        for(auto it: skill){
            if(mp.find(it)==mp.end()) continue;
            mp[it]--;

            int temp=skillTeam-it
            if(mp.find(temp)!=mp.end()){
                mp[temp]--;
                if(mp[temp]==0) mp.erase(temp);
                ans+=it*temp;
                if(mp[it]==0) mp.erase(it);
            }
        }

        if(mp.size()>0) return -1;
        return ans;
    }
};

class Solution:
    def longestValidSubstring(self, word: str, forbidden: List[str]) -> int
        trie = {}
        for f in forbidden:
            t = trie
            for c in f:
                if c not in t:
                    t[c] = {}
                t = t[c]
            t["end"] = True

        def isForbidden(s):
            t = trie
            counter = 0
            for c in s:
                if c not in t:
                    return False
                t = t[c]
                counter += 1
                if "end" in t:
                    return counter
            return False

        res = 0
        j = len(word)
        for i in range(len(word) - 1, -1, -1):
            truc = isForbidden(word[i:j])
            if truc:
                j = i + truc - 1
            res = max(res, j - i)
        return res

class Solution:
    def totalNQueens(self, n: int) -> int:
        def addans(board,ans):
            temp=[]
            for row in board:
                for j in range(len(row)):
                    if row[j]=="Q"
                        temp.append(j+1)
            ans.append(temp)
        def solve(col,board,low,upper,lower,ans,n):
            if col==n:
                addans(board,ans)
                return 
            for row in range(n):
                if low[row]==0 and upper[n-1+col-row]==0 and lower[row+col]==0:
                    board[row][col]="Q"
                    low[row]=1
                    upper[n-1+col-row]=1
                    lower[row+col]=1
                    solve(col+1,board,low,upper,lower,ans,n)
                    low[row]=0
                    upper[n-1+col-row]=0
                    lower[row+col]=0
        ans=[]        
        board=[[0]*n for i in range(n)]
        low=[0]*n
        upper=[0]*(2*n-1)
        lower=[0]*(2*n-1)
        solve(0,board,low,upper,lower,ans,n)
        return len(ans)

