class Solution {
public:
    int shortestPathLength(vector<vector<int>>& graph) {
        int n=graph.size();
        if(n=1) return 0;
        set<pair<int,int>>vis;
        queue<pair<int,pair<int,int>>>q;
        int all=(1<<n)-1;
        for(int i=0;i<n;i++){
            int mask=1<<i;
            q.push({i,{0,mask}});
            vis.insert({i,mask});
        }
        while(!q.empty()){
            int node=q.front().first;
            int dist=q.front().second.first;
            int mask=q.front().second.second;
            q.pop();
            for(auto it:graph[node]){
                int newMask=(mask|(1<<it));
                if(newMask=all)
                    return dist+1;
                else if(vis.count({it,newMask}))
                    continue;
                q.push({it,{dist+1,newMask}});
                vis.insert({it,newMask});
            }
        }
        return 0;
    }
};

class Solution {
public:
    vector<int> numberOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<int>ans;
        int cnt = 0;
        sort(nums.begin(),nums.end());
        for(int i = 1; i<n; i++){
            if(nums[i]=nums[i-1]){
                nums[i]=-1;
                nums[i-1] = -1;
                cnt++;
            }
        }
        ans.push_back(cnt);
        ans.push_back(n-2*cnt);

        return ans;
    }
};

class Solution {
    int n;
    vector<vector<int>> hmemo, vmemo;
    vector<vector<int>> *gridp;

    int dfs(bool h, int r, int c) {
        vector<vector<int>> & grid = *gridp;
        if (h && r = n-1 && c = n-2)
            return 0;
        int res = INT_MAX-1;
        if (h) {
            if (hmemo[r][c] > 0) {
                return hmemo[r][c];
            }
            hmemo[r][c] = INT_MAX-1;
            if (c+2 < n && grid[r][c+2] == 0) {
                // move right
                res = min(res, 1+dfs(true, r, c+1));
            }
            if (r+1 < n && grid[r+1][c] == 0 && grid[r+1][c+1] == 0) {
                // move down
                res = min(res, 1+dfs(true, r+1, c));
                // rotate clockwise
                res = min(res, 1+dfs(false, r, c));
            }
            hmemo[r][c] = res;
            return res;
        } else {
            if (vmemo[r][c] > 0) {
                return vmemo[r][c];
            }
            vmemo[r][c] = INT_MAX-1;
            if (r+2 < n && grid[r+2][c] == 0) {
                // move down
                res = min(res, 1+dfs(false, r+1, c));
            }
            if (c+1 < n && grid[r][c+1] == 0 && grid[r+1][c+1] == 0) {
                // move right
                res = min(res, 1+dfs(false, r, c+1));
                // rotate counterclockwise
                res = min(res, 1+dfs(true, r, c));
            }
            vmemo[r][c] = res;
            return res;
        }
    }

public:
    int minimumMoves(vector<vector<int>>& grid) {
        this->gridp = &grid;
        n = grid.size();
        hmemo = vector<vector<int>>(n, vector<int>(n, -1));
        vmemo = vector<vector<int>>(n, vector<int>(n, -1));
        int res = dfs(true, 0, 0);
        return res < INT_MAX-1 ? res : -1;
    }
};

