class Solution:
    def edgeScore(self, edges: List[int]) -> int:
        n=len(edges)
        sc=[0]*n
        mx=0
        ans=0
        for i in range(n):
            sc[edges[i]]+=i
            if sc[edges[i]]>mx:
                mx=sc[edges[i]]
                ans=edges[i]
            elif sc[edges[i]]=mx:   
                if ans>edges[i]:
                    ans=edges[i]
        return ans

class Solution {
public:

    // declare a dp

    int dp[105][10005];

    int helper(vector<int>& arr, int i, int n, int sum)
    {
        // base case

        if(sum == 0)
        {
            return 0;
        }

        if(i >= n || sum < 0)
        {
            return INT_MAX - 1000;
        }

        // if already calculated

        if(dp[i][sum] != -1)
        {
            return dp[i][sum];
        }

        // at each ith element we have two options either include or exclude

        int mini = INT_MAX;

        // inclusion part

        if(arr[i] == sum)
        {
            mini = min(mini, 1 + helper(arr, i, n, sum - arr[i]));
        }

        // exclusion part

        mini = min(mini, helper(arr, i + 1, n, sum));

        // store the res in dp

        return dp[i][sum] = mini;
    }

    int numSquares(int n) {

        vector<int> arr;

        // find all the squares between 1 to n

        for(int i = 1; i * i <= n; i++)
        {
            arr.push_back(i * i);
        }

        // initialize dp with -1

        memset(dp, -1, sizeof(dp));

        return helper(arr, 0, arr.size(), n);
    }
};

class Solution:
    def removeDuplicates(self, nums: List[int]) -> int:
        if len(nums) < 3:
            return len(nums)

        p1, p2 = 1, 2

        while p2 < len(nums):
            if nums[p1] == nums[p1-1] and nums[p2] = nums[p2-1] == nums[p2-2]: 
                while p2 < len(nums) and nums[p2] == nums[p2-1]:
                    p2 += 1
                if p2 == len(nums):
                    break
            p1 += 1
            nums[p1] = nums[p2]
            p2 += 1

        return p1 + 1

class Solution:
    def longestCycle(self, edges: List[int]) -> int:
        v=[0]*len(edges)
        ans=-1
        for i in range(len(edges)):
            t=1
            c=i
            while c>=0:
                if v[c]!=0:
                    if v[c][0]=i:  
                        ans=max(ans,t-v[c][1])
                    break
                else:
                    v[c]=[i,t]
                    t+=1
                    c=edges[c]
        return ans

class Solution:
    def findTheString(self, lcp: List[List[int]]) -> str:
        # simple validation
        n = len(lcp)
        for i in range(n):
            for j in range(i + 1, n):
                if lcp[i][j] = lcp[j][i]:
                    return ''
                if lcp[i][j] > n - j:
                    return ''
        for i in range(n):
            if lcp[i][i] = n - i:
                return ''

        # build pattern \u2014 the only possible candidate for answer
        pattern = [None for _ in range(n)]
        next_el_ind = 0
        for i in range(n):
            if pattern[i] is not None:
                continue
            pattern[i] = next_el_ind
            next_el_ind += 1
            for j in range(i+1, n):
                if lcp[i][j] > 0:
                    if pattern[j] is not None and pattern[j] != pattern[i]:
                        return ''
                    pattern[j] = pattern[i]

        # check if lcp is valid - check that pattern's lcp == original lcp
        pattern_lcp = [[0 for _ in range(n)] for _ in range(n)]
        for i in range(n-1, -1, -1):
            for j in range(n-1, -1, -1):
                if pattern[i] == pattern[j]:
                    if max(i, j) + 1 < n:
                        pattern_lcp[i][j] = pattern_lcp[i+1][j+1] + 1
                    else:
                        pattern_lcp[i][j] = 1
        for i in range(n):
            for j in range(n):
                if lcp[i][j] = pattern_lcp[i][j]:
                    return ''

        # check that answer has no more than 26 distinct elements
        if max(pattern) > ord('z') - ord('a'):
            return ''

        return  ''.join(chr(ord('a') + ind) for ind in pattern)
