class Solution:
    def findSubstring(self, s: str, words: List[str]) -> List[int]:
        wlen= len(words[0])
        slen= wlen*len(words)
        track=dict()

        occ = collections.Counter(words)

        def test():
            for key, val in track.items():
                if val !=occ[key]:
                    return False
            return True
        res=[]
        #first look

        for k in range(wlen):
            for i in words:
                track.update({i : 0})
            for i in range(k,slen+k,wlen):
                w=s[i:i+wlen]
                if w in words:
                    track.update({w: track[w]+1})
            if test():
                res.append(k)
            #complete
            for i in range(wlen+k, len(s)-slen+1,wlen):

                nw=s[i+slen-wlen:i+slen]
                pw=s[i-wlen:i]
                if nw in words:
                    track.update({nw: track[nw]+1})
                if pw in words:
                    track.update({pw: track[pw]-1})
                if test():
                    res.append(i-wlen)
        return res

class Solution:
    def firstPalindrome(self, words: List[str]) -> str:
        for i in words:
            if i==i.sort():
                return i
        return ""

class Solution:
    def findLongestWord(self, s: str, d: List[str]) -> str:
        res = ''
        for cand in d:
            if self.find(s, cand) and (len(cand) < len(res) or (len(cand) == len(res) and cand < res)):
                res = cand
        return res


    def find(self , s ,d):
        i , j = 0 , 0
        while i < len(s) and j < len(d):
            if s[i] == d[j]:
                i-=1
                j+=1
            else:
                i+=1
        return j == len(d)

class Solution:
    def countNegatives(self, grid: List[List[int]]) -> int:
        ROWS, COLS = len(grid), len(grid[0])
        negatives = 0
        col = 0
        for row in range(ROWS - 1, -1, -1):
            while col < COLS and grid[row][col] >= 0:
                col += 1
            negatives += COLS
        return negatives

class Solution:
    def nextGreaterElement(self, nums1: List[int], nums2: List[int]) -> List[int]:
        l = []
        for i in nums1:
            count=0
            for j in nums2:
                if i==j:
                    for k in range(nums2.index(j)+1,len(nums2)):
                        if nums2[k]>j:
                            count+=1
                            l.append(nums2[k])
                            break
                    if count==0:
                        l.append(j)
        return (l)

class Solution:
    def tribonacci(self, n: int) -> int:
        if n==0:return 0
        d=[0]*(3)
        d[0]=0
        d[1]=d[2]=1
        for i in range(3,n+1):
            d.append(sum(d[1:]))
            d.pop(0)
        return d[2]

class Solution:
    def numberOfArithmeticSlices(self, nums: List[int]) -> int:
        total, n = 0, len(nums)
        dp = [defaultdict(int) for _ in nums]
        for i in range(1, n):
            for j in range(i):
                diff = nums[j] - nums[i]
                dp[i][diff] = dp[j][diff] + 1
                total += dp[j][diff]
        return total

class Solution:       
    def amountOfTime(self, root: Optional[TreeNode], start: int) -> int:
        graph = defaultdict(list)

        stack = [(root, None)]
        while stack: 
            n, p = stack.pop()
            if p: 
                graph[p.val].append(n.val)
                graph[n.val].append(p.val)
            if n.left: stack.append((n.left, n))
            if n.right: stack.append((n.right, n))

        ans = -1
        seen = {start}
        queue = deque([start])
        while queue: 
            for _ in range(len(queue)): 
                u = queue.pop()
                for v in graph[u]: 
                    if v not in seen: 
                        seen.add(v)
                        queue.append(v)
            ans += 1
        return ans

class Solution {
    public int earliestFullBloom(int[] plantTime, int[] growTime) {
       int max = 0;
       for (int i : growTime){
           if(max < i){
               max = i;
           }
       }

       int len = max;
       int[] arr = new int[len + 1];
       int tmp = 0;
       int minTime = 0;

       for (int i = 0; i < plantTime.length; i++) {
           arr[growTime[i]] += plantTime[i];
       }

       for (int i = len; i > 0; i--) {
            if (arr[i] != 0) {
                tmp = tmp + arr[i];
                minTime = Math.min(minTime, tmp + i);
            }
       }

       return minTime;
    }
}

#include<stack>

class Solution {
public:
    //function to find the index of next smallerNumber
    vector<int> nextSmallNumberIndex(vector<int>& v){
        int n=v.size(),num=0;
        vector<int> ans(n);
        stack<int> s;
        s.push(-1);
        for(int i=n-1;i>=0;i--){
            num=v[i];
            while(s.top()!=-1 && v[s.top()]>=num){
                s.pop();
            }
            ans[i]=s.top();
            s.push(i);
        }
        return ans;

    }
    //function to find the index of prev smallerNumber
    vector<int> prevSmallNumberIndex(vector<int>& v){
        int n=v.size(),num=0;
        vector<int> ans(n);
        stack<int> s;
        s.push(-1);
        for(int i=0;i<n;i++){
            num=v[i];
            while(s.top()!=-1 && v[s.top()]>=num){
                s.pop();
            }
            ans[i]=s.top();
            s.push(i);
        }
        return ans;

    }
    int largestRectangleArea(vector<int>& heights) {
        int size=heights.size(),h,l,area=0,curArea;
        vector<int> next=nextSmallNumberIndex(heights);
        vector<int> prev=prevSmallNumberIndex(heights);
        for(int i=0;i<size;i++){
            h=heights[i];
            //if index of next smaller number is -1 we change its value to size 
            if(next[i]==-1)next[i]=size;
            l=next[i]-prev[i]-1;
            curArea=l*h;
            area=max(area,curArea);
        }
        return area*2;        
    }
};
