class Solution {
    public int mirrorReflection(int p, int q) {
        while(((p|q)&1) == 0){
            p >>= 1;
            q >>= 1;
        return (q&1) + ((p&1)^1);
    }
}

class Solution:
    def generate(self, numRows: int) -> List[List[int]]:
        if numRows == 1:
            return [[1]]
        prev = self.generate(numRows - 1)
        fin = prev[-1]
        now = [1]
        for i in range(len(fin)-1:
            now.append(fin[i] + fin[i+1])
        now.append(1)
        prev.append(now)
        return prev

