class Solution {
public:
    int myAtoi(string s) {
        long long res = 0; // stores our integer value
        bool neg = 0; // checks whether integer will be -ve or +ve
        bool leading = true; // true if theres leading spaces 

        int n = s.size();

        for (int i = 0; i<n; i++) {
            if (s[i] == ' ') { // checks for leading spaces
                if (leading) continue;
                else break;
            }
            else if (s[i] == '-') { // checks -ve 
                if (i+1 < n && isdigit(s[i+1])) neg = 1;
                else break; // break if next char is non-digit
            }
            else if (s[i] == '+') { // checks for +ve
                if (i+1 < n && isdigit(s[i+1])) neg = 0;
                else break; // break if next char is non-digit
            }
            else if (!isdigit(s[i])) break; // break on encoutring non-digit
            else { // storing our integer value to res
                if (res < 1e11) {
                    res = res*10 + (s[i]-'0');
                    if (i+1 < n && !isdigit(s[i+1])) 
                        break;// break if next char is non-digit
                }
                else break;
                leading = false; // tells that integer has been encoutered
            }
        }

        if (res > INT_MAX && neg) return "INT_MIN";
        if (res > INT_MAX) return "INT_MAX";

        return neg ? -1*res : res;
    }
};
