class DSU:
    def __init__(self, n):
        self.par = list(range(n))
        self.rank = [1] * n
        self.size = 1
    def find(self, u):
        if u != self.par[u]:
            self.par[u] = self.find(self.par[u])
        return self.par[u]
    def union(self, u, v):
        uu, vv = self.find(u), self.find(v)
        if uu == vv:
            return False
        if self.rank[uu] > self.rank[vv]:
            self.par[vv] = uu
        elif self.rank[vv] > self.rank[uu]:
            self.par[uu] = vv
        else:
            self.par[uu] = vv
            self.rank[vv] += 1
        self.size += 1
        return True
    def issizemorethanone(self):
        return self.size > 1

