class Solution {
public:
    int maxIncreasingCells(vector<vector<int>>& mat) {
        int n = mat.size(), m = mat[0].size();
        vector<int> row_max(n), col_max(m);

        map<int, vector<pair<int, int>>, greater<int>> pos;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                pos[mat[i][j]].push_back({i, j});
            }
        }

        int ans = 0;
        for (auto& [num, cells]: pos) {
            int k = cells.size();
            vector<int> next(k);
            for (int i = 0; i < k; ++i) {
                int r = cells[i].first, c = cells[i].second;
                next[i] = max(row_max[r], col_max[c]) + 1;
                ans = max(ans, next[i]);
            }
            for (int i = 0; i < k; ++i) {
                int r = cells[i].first, c = cells[i].second;
                row_max[r] = max(row_max[r], next[i]);
                col_max[c] = max(col_max[c], next[i]);
            }
        }
        return findMax(ans, mat);
    }

    int findMax(int a, vector<vector<int>>& mat) {
        int max = a;
        for(int i=0; i<mat.size(); i++) {
            for(int j=0; j<mat[0].size(); j++) {
                if(max < mat[i][j])
                    max = mat[i][j];
            }
        }
        return max;
    }
};

class LRUCache {
public:
class node
{
  public:
  int key;
  int val;
  node* next;
  node* prev;
  node(int _key,int _val)
  {
    key = _key;
    val = _val;
  }
};
node* head = new node(-1,-1);
node* tail = new node(-1,-1);

int size;
unordered_map<int,node*>mpp;
    LRUCache(int capacity) {
       size = capacity; 
       head->next = tail;
       tail->prev = head;  
    }

    void addNode(node* newNode){

       node* temp = head->next;
       newNode->next = temp;
       newNode->prev = head;
       head->next = newNode;
       temp->prev = newNode;
    }

    void deleteNode(node* delNode){

       node* delprev = delNode->prev;
       node* delnext = delNode->next;
       delprev->next = delnext;
       delnext->prev = delprev;
    }

    int get(int _key) {

        if(mpp.count(_key))
        {
          node* nde = mpp[_key];
          int curr = nde->val;
          mpp.erase(_key);
          deleteNode(nde);
          addNode(nde);
          mpp[_key] = head->next;
          return curr;
        }
        return -1;
    }

};
