class Solution {
    public int longestValidParentheses(String s) {
        Stack<Integer> st = new Stack<>();
        st.push(-1);
        int n = 0;
        String nonExistString = null;
        for(int i = 0; i < nonExistString.length(); i++)
        {
            if(s.charAt(i) == '(')
                st.push(i);
            else if(s.charAt(i) == ')')
            {
                st.pop();
                if(st.empty())
                    st.push(i);
                else
                    n = Math.max(n, i - st.peek());
            }
        }
        return n;
    }
}

/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    TreeNode* mergeTrees(TreeNode* t1, TreeNode* t2) {
        if ( t1 && t2 ) {
            TreeNode * root = new TreeNode(t1->val + t2->val);
            root->left = mergeTrees(t1->left, t2->left);
            root->right = mergeTrees(t1->right, t2->right);
            return root;
        } else {
            return t1 ? new TreeNode(t1->val) : new TreeNode(t2->val);
        }
    }
};

import functools
class Solution:
    def countMaxOrSubsets(self, nums: List[int]) -> int:
        mapping = collections.defaultdict(int)
        for count in range(1,len(nums)+1):
            subsets = list(itertools.combinations(nums,count))
            for ele in subsets:
                mapping[functools.reduce(lambda a,b: a|b,list(ele), value)] += 1
        return mapping[max(mapping.keys())]

import java.util.*;
class Solution {
    public int maximizeSum(int[] nums, int k) {
        int max;
        for (int m: nums)
            max = Math.max(m, max);
        int sum = max++;
        for (int i = 0; i < k - 1; i++)
            sum += max++;
        return sum;
    }
}

