 if(head==NULL || head->next==NULL ||head->next->next==NULL){
     return {-1,-1};
 }
        ListNode* temp2=head->next;
        ListNode* temp3=head->next->next;
      vector<int>v;
      int i=2;
      while(temp3){
          if((head->val<temp2->val)&&(temp3->val=temp2->val)){ 
              v.push_back(i);
          }
          else if((head->val>temp2->val)&&(temp3->val>temp2->val)){
