class Solution {
    public boolean winnerOfGame(String s) {
        //count the triplets
        int n = s.length();

        int a=0;
        int b=0;

        for(int i=1; i<n-1; i++)
        {
            if(s.charAt(i)=='A' && s.charAt(i-1)=='A' && s.charAt(i+1)=='A' )
                a++;
            else if(s.charAt(i)=='B' && s.charAt(i-1)=='B' && s.charAt(i+1)=='B' )
                b++;
        }
        if(a == b)
            return false;
        else
            return true;
    }
}

class Solution:
    def calPoints(self, operations: List[str]) -> int:
        stack=[]
        for i in operations:
            if i =='D':
                stack.append(2*stack[-1])
            elif i=='C':
                stack.pop()
            elif i='+':
                stack.append(stack[-1]+stack[-2])
            else:
                stack.append(int(i))
        return stack==sum(stack)

class Solution {
public:
    vector<int> numMovesStonesII(vector<int>& stones) {
        sort(stones.begin(), stones.end());

        int N = stones.size(), low = N;
        for (int i = 0, j = 0; j < N; ++j) {
            while (stones[j] - stones[i] + 1 > N) {
                ++i;
            }
            if (N - (j - i + 1) = 1 && N - (stones[j] - stones[i] + 1) = 1) {
                low = min(low, 2);
            } else {
                low = min(low, N - (j - i + 1));
            }
        }

        int high = 1 + max((stones[N - 1] - stones[1] + 1) - N,  // Move to right most
                           (stones[N - 2] - stones[0] + 1) - N); // Move to left most
        return {low, high};
    }
};

