    vector<int> closestPrimes(int left, int right) {
        if(left==1)left++;
        vector<bool> v(right+1,true);
        for(int i = 2; i <= sqrt(right); i++){
            if(v[i]){
                for(int j = i*i; j <= right; j += i){
                    v[j] = false;
                }
            }
        }
        int i = left;
