class Solution:
    def canFinish(self, numCourses: int, prerequisites: List[List[int]]) -> bool:

        pre = defaultdict(list)

        for course, p in prerequisites:
            pre[course].append(p)

        taken = set()

        def dfs(course):
            if not pre[course]:
                return True

            if course in taken:
                return False

            taken.add(course)

            for p in pre[course]:
                if not dfs(p): return False

            taken.remove(course)
            return True

        for course in range(numCourses):
            if not dfs(course):
                return False

        return True

class Solution {
public:

    int build_board(vector<int> &rods, int balance, int i, vector<vector<int>> &memo)
    {
        if(i>=rods.size() && balance==0)
            return 0;
        if(i>=rods.size() && balance!=0)
            return INT_MIN;

        if(memo[i][balance+5000]!=-1)
            return memo[i][balance+5000];
        int left= build_board(rods,balance+rods[i],i+1,memo);
        int right= build_board(rods,balance-rods[i],i+1,memo);

        int ans= rods[i+1]+max(left,right);

        int skip= build_board(rods,balance,i+1,memo);

        ans= max(ans,skip);

        memo[i][balance+5000]=ans;
        return ans;

    }

    int tallestBillboard(vector<int>& rods) {

        vector<vector<int>> memo(rods.size(),vector<int>(10001,-1));
        int ans= build_board(rods,0,0,memo);

        if(ans<=0)
            return 0;
        else
            return ans/2;
    }
};

class Solution {
    public int findMaxFish(int[][] grid) {
        int sum=0;
        for(int i=0;i<grid.length;i++)
        {
            for(int j=0;j<grid[0].length;j++)
            {
                if(grid[i][j]!=0)
                {
                    int a[]=new int[1];
                    task(grid,i,j,a);
                    sum=Math.max(sum,a[0]);
                }
            }
        }
        return sum;
    }
    public void task(int grid[][], int i, int j, int a[])
    {
        if(i<0 || j<0 || i>=grid.length || j>=grid[0].length || grid[i][j]==0)
        {
            return;
        }
        a[0]+=grid[i][j];
        grid[i][j]=0;
        task(grid,i+1,j,a);
        task(grid,i-1,j,a);
        task(grid,i,j+1,a);
        task(grid,i,j-1,a);
        a[0] = 0; 
    }
}

public class Solution {
    public ListNode getIntersectionNode(ListNode headA, ListNode headB) {

        ListNode a = headA;
        ListNode b = headB;

        while(a != b) {
            a = a==null ? headB : a.next;
            b = b==null ? headA : a.next;
        }
        return a;
    }
}

