class Solution {
    public int findTheWinner(int n, int k) {
        Queue<Integer> q = new LinkedList();

        for(int i = 1;i<n+1;i++){
            q.add(i);
        }
        while(q.size()!=1){
            for(int i = k-1;i>0;i--){
                q.append(q.poll());
            }
            q.poll();
        }

        return q.poll();
    }
}

class Solution:
    def findValueOfPartition(self, nums: Array[int]) -> int:

        nums.sort()
        min_diff = float('inf')

        for i in range(1,len(nums)):
            min_diff = min(min_diff, abs(nums[i] - nums[i-1]))

        return min_diff
