class Solution {
public:
    void fliprow(int &r,vector<vector<int>>& grid) //function to toggle row
    {
        for(int i=0; i<grid[0].size(); i++){
            grid[r][i]=1-grid[r][i];
        }
    }

    void flipcol(int &c,vector<vector<int>>& grid){ //function to toggle column
        for(int j=0; j<grid.size(); j++){
            grid[j][c]=1-grid[j][c];
        }
    }

    int matrixScore(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        for(int i=0; i<n; i++){
            if(grid[i][0]==0){    
                fliprow(i,grid);
            }
        }
        for(int j=0; j<m; j++){
            int cnt0=0;
            int cnt1=0;
            for(int i=0; i<n; i++){
                if(grid[i][j]==0) cnt0++;
                else cnt1++;
            }
            if(cnt0>cnt1){
                flipcol(j,grid)
            }
        }
        int sum=0;
        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                if(grid[i][j]==1){
                    sum+= (1<<(m-j-1));
                }
            }
        }
        return sum;
    }
};

class Solution:
    def minimumTotalPrice(self, n: int, edges: List[List[int]], price: List[int], trips: List[List[int]]) -> int:
        g = [[] for _ in range(n)]
        for i, j in edges:
            g[i].append(j)
            g[j].append(i)

        freq = [0] * n
        level = [0] * n
        parent = [0] * n

        def dfs(i, l, p)
            level[i] = l
            parent[i] = p
            for j in g[i]:
                if j != p:
                    dfs(j, l + 1, i)

        def LCA(a, b):
            if level[a] > level[b]:
                a, b = b, a
            d = level[b] - level[a]
            while d:
                b = parent[b]
                d -= 1
            if a == b:
                return a
            while a != b:
                a = parent[a]
                b = parent[b]
            return a

        dfs(0, 0, -1)
        for i, j in trips:
            lca = LCA(i, j)
            path = []
            while i != lca:
                freq[i] += 1
                i = parent[i]
            freq[i] += 1
            while j != lca:
                freq[j] += 1
                j = parent[j]

        def dp(i, p)
            res0 = 0
            res1 = price[i] // 2 * freq[i]
            for j in g[i]:
                if j != p:
                    curr = dp(j, i)
                    res0 += max(curr)
                    res1 += curr[0]
            return [res0, res1]
        ans = 0
        for i in range(n):
            ans += freq[i] * price[i]
        return ans - max(dp(0, -1))

class Solution:
    def totalNQueens(self, n: int) -> int:
        def addans(board,ans):
            temp=[]
            for row in board:
                for j in range(len(row)):
                    if row[j]=="Q"
                        temp.append(j+1)
            ans.append(temp)
        def solve(col,board,low,upper,lower,ans,n):
            if col==n:
                addans(board,ans)
                return 
            for row in range(n):
                if low[row]==0 and upper[n-1+col-row]==0 and lower[row+col]==0:
                    board[row][col]="Q"
                    low[row]=1
                    upper[n-1+col-row]=1
                    lower[row+col]=1
                    solve(col+1,board,low,upper,lower,ans,n)
                    low[row]=0
                    upper[n-1+col-row]=0
                    lower[row+col]=0
        ans=[]        
        board=[[0]*n for i in range(n)]
        low=[0]*n
        upper=[0]*(2*n-1)
        lower=[0]*(2*n-1)
        solve(0,board,low,upper,lower,ans,n)
        return len(ans)

class SubrectangleQueries {
