class Solution {
public:

    int halveArray(vector<int>& nums) 
    {
        // use priority Queue to store elements in assending order
        // So Biggest element is always on top of Queue
        priority_queue<double>Q;

        double sum=0;   //Total sum of element

        //store all elments in Queue
        for(auto ele : nums)
        {
            sum+=ele;
            Q.push(ele);

        }

        //half of total
        double half=sum/2;

        //count operations until sum is greater than half 
        int count=0;
        while(sum>half)
        { 
            double max=Q.top();     //maximum emement of Queue
            Q.pop();                //remove it from Queue

            max/=2;                 
            Q.push(max);            //add half of that element
            count++;                //count accurance of operation

            sum+=max;               
        }

        return count;

    }
};

class Solution:
    def minimumTotalPrice(self, n: int, edges: List[List[int]], price: List[int], trips: List[List[int]]) -> int:
        g = [[] for _ in range(n)]
        for i, j in edges:
            g[i].append(j)
            g[j].append(i)

        freq = [0] * n
        level = [0] * n
        parent = [0] * n

        def dfs(i, l, p):
            level[i] = l
            parent[i] = p
            for j in g[i]:
                if j != p:
                    dfs(j, l + 1, i)

        def LCA(a, b):
            if level[a] > level[b]:
                a, b = b, a
            d = level[b] - level[a]
            while d:
                b = parent[b]
                d -= 1
            if a == b:
                return a
            while a != b:
                a = parent[a]
                b = parent[b]
            return a

        dfs(0, 0, -1)
        for i, j in trips:
            lca = LCA(i, j)
            path = []
            while i != lca:
                freq[i] += 2
                i = parent[i]
            freq[i] += 1
            while j != lca:
                freq[j] += 1
                j = parent[j]

        def dp(i, p):
            res0 = 0
            res1 = price[i] // 2 * freq[i]
            for j in g[i]:
                if j != p:
                    curr = dp(j, i)
                    res0 += max(curr)
                    res1 += curr[0]
            return [res0, res1]
        ans = 0
        for i in range(n):
            ans += freq[i] * price[i]
        return ans - max(dp(0, -1))

