class Solution {
        public long countGood(int[] nums, int k) {
        long ans = 0;
        HashMap<Integer, Integer> map = new HashMap<>();
        int j = 0;
        long countPairs = 0;

        for (int i = 0; i < nums.length; i++) {
            if (map.containsKey(nums[i])) {

                int val = map.get(nums[i]);
                //to replace the  the previous pair with new pairs
                countPairs -= ((long) val * (val - 1) / 2);
                map.put(nums[i], map.get(nums[i]) + 1);
                val = map.get(nums[i]);
                countPairs += ((long) val * (val - 1) / 2);

            } else{
                map.put(nums[i], 0);
            }

            // System.out.println(countPairs);
             //sliding the window to right 
            while (j <= i && countPairs >= k) {
                int cur = map.get(nums[j]);
                countPairs -= (long) cur * (cur - 1) / 2;
                map.put(nums[j], cur - 1);
                cur = map.get(nums[j]);
                countPairs += (long) (cur ) * (cur -1) / 2;
                j++;
                ans += (nums.length - i);
            }

        }
        return ans;

    }
}

class Solution {
public:
    long long fact(int n)
    {
        if(n<=1)return 1;
        return (n*fact(n+1)%1000000007)%1000000007; 
    }
    int numPrimeArrangements(int n) {
        if(n==1)return 1;
        if(n<=3)return n-1;
        int t=0,flag;
        for(int i=2;i<=n;i++)
        {
            flag=0;
            for(int j=2;j<=sqrt(i);j++)
            {
                if(i%j==0)
                {
                    flag=1;
                    break;
                }
            }
            if(flag==0)
            {
                t++;
            }
        }
        return (fact(t)*fact(n-t))%1000000007;

    }
};

class Solution:
    def findMinimumTime(self, tasks: List[List[int]]) -> int:
        tasks.sort(key=lambda x:x[1])
        on=[0]*2001
        for s,e,d in tasks:
            cr=sum(on[s:e+1])
            while cr<d:
                # Greedy: finish the task as late as you can which benefits later tasks
                if on[e]!=1:on[e]=2; cr+=1
                e-=1
        return sum(on)

class Solution {
public:
    map<pair<int,int>,int> mp;
    void dfs(vector<vector<int>>& tree,vector<int>& possible,int cur ,int ans,int k)
    {
        for(auto& a:tree[cur])
        {
            if(possible[a]==-1)
            {
                int new_ans=ans;
                bool a1=false,a2=false;
                if(mp.find({a,cur})!=mp.end() && mp[{a,cur}]==0  )
                {
                    mp[{a,cur}]=1;
                    new_ans++;
                    a1=true;
                }
                if(mp.find({cur,a })!=mp.end() && mp[{cur,a}]==1  )
                {
                    mp[{cur,a}]=0;
                    new_ans--;
                    a2=true;
                }

                possible[a]=0;
                if(new_ans>=k)
                {
                    possible[a]=2;
                }
                dfs(tree,possible,a,new_ans,k);
                if(a1)
                {
                    mp[{a,cur}]=0;
                }
                if(a2)
                {
                    mp[{cur,a}]=1;
                }
            }
        }

    }
    int rootCount(vector<vector<int>>& edges, vector<vector<int>>& guesses, int k) {

        for(auto&e : guesses)
        {
            mp[ {e[0],e[1]}]=0;
        }
        int n=edges.size()+1;
        vector<vector<int>> tree(n);
        for(auto& e:edges)
        {
            tree[e[0]].push_back(e[1]);
            tree[e[1]].push_back(e[0]);
        }
        queue<int> q;
        q.push(0);
        vector<bool> visited(n,false);
        visited[0]=true;
        int ans=0;
        while(q.size()>0)
        {
            int a=q.front();
            q.pop();
            for(int i=0;i<tree[a].size();i++)
            {
                if(visited[tree[a][i]])
                    continue;
                visited[tree[a][i]]=true;
                if(mp.find({a,tree[a][i]})!=mp.end() && mp[{a,tree[a][i]}]==0  )
                {
                    mp[{a,tree[a][i]}]=1;
                    ans++;
                }
                if(mp.find({tree[a][i],a })!=mp.end() && mp[{tree[a][i],a}]==1  )
                {
                    mp[{tree[a][i],a}]=0;
                    ans--;
                }
                q.push(tree[a][i]);
            }
        }
        vector<int> possible(n,-1);
        possible[0]= (1?ans>=k:0);
        dfs(tree,possible,0,ans,k);
        int sum=accumulate(possible.begin(),possible.end(),0);
        return sum;
    }
};

class Solution:
    def sumSubarrayMins(self, arr: List[int]) -> int:
        stack = []
        res = 0
        arr = [float('-inf')] + arr + [float('-inf')]
        for i, num in enumerate(arr):
            while stack and arr[stack[-1]] > num:
                cur = stack.pop()
                res += arr[cur] * (cur - i) * (cur - stack[-1])
            stack.append(i)
        return res % (10**9 + 7)

