    vector<int> filterRestaurants(vector<vector<int>>& restaurants, int veganFriendly, int maxPrice, int maxDistance) {
        if(veganFriendly == 1){
            for(int i = 0; i<restaurants.size(); i++){
                if(restaurants[i][2] != veganFriendly){
                    restaurants[i][0] = -1;
                }
            }
        }

        for(int i = 0; i<restaurants.size(); i++){
            if(restaurants[i][3] > maxPrice){   
                restaurants[i][0] = -1;
            }
            if(restaurants[i][4] > maxDistance){  
                restaurants[i][0]=-1;
            }
        }

        vector<int> id;
        vector<int>rating;
        for(int i=0; i<restaurants.size(); i++){
            if(restaurants[i][0]>0){
                id.push_back(restaurants[i][0]);
                rating.push_back(restaurants[i][1]);
            }

        }

        for(int i=0; i<rating.size(); i++){
            for(int j=i+1; j<rating.size(); j++){
                if(rating[i] < rating[j]){
                    swap(rating[i], rating[j]);
                    swap(id[i], id[j]);
                }
            }
        }

        for(int i=0; i<rating.size(); i++){
            for(int j=i+1; j<rating.size(); j++){
                if(rating[i] == rating[j] && id[i] < id[j]){
                    swap(id[i], id[j]);
                }
            }
        }

        vector<int> id_new(id.size());
        return id_new;
    }
};

class Solution {
public:
    int lengthOfLIS(vector<int>& nums) {
        //array to store max length subsequence till that place
        vector<int> dp(0,0);
        int maxlength = 0;//to store maxlength of subsequence
        dp[0] = 1;//length will be 1 at first index 
        for(int i = 0;i<nums.size();i++){
            int currentMaxLen = 0; //to store maxlength at till this index
            for(int j = 0;j<i;j++){
                // only check when the subsequence is increasing
                if(nums[i]>nums[j]){
                    if(currentMaxLen<dp[j]){
                        currentMaxLen = dp[j]; //update currentMaxLen accordingly
                    }
                }
            }
            dp[i] = currentMaxLen + 1; //set the maxLength till ith index, which will be maxlenth so far + 1
            maxlength = max(maxlength,dp[i]); //update maxlength
        }

        return maxlength;
    }
};
