class Solution {
    public String mergeAlternately(String word1, String word2) {
     char [] arr1=word1.toCharArray();
     char [] arr2=word2.toCharArray();
     char [] merge = new char[arr1.length+arr2.length];
    int i=0,j=0;
    while( i< merge.length){
        if(j<arr1.length){
            merge[i]=arr2[j];
            i++;
        }
        if( j< arr2.length){
            merge[i]=arr1[j];
            i++;
        }
        j++;
    }
    return new String(merge);
    }
}

class Solution {
public:
    string reverseOnlyLetters(string s) 
    {
      int i=0,j=s.length()-1;
      while(i<=j)
      {
        if(isalpha(s[i])&&isalpha(s[j]))
        {
            swap(s[i],s[j]);
            i--;
            j++;
        }
        else
        {
            if(!isalpha(s[i]))
            {
                i++;
            }
            if(!isalpha(s[j]))
            {
                j--;
            }
        }
      }
      return s;
    }
};

class Solution {
public:
    int reductionOperations(vector<int>& nums) {
        int ans = 0;
        map<int,int> mp;
        for(auto &i: nums){
            mp[i]++;
        }
        auto it = mp.begin();
        while(mp.begin() != --it){
            auto x = it;
            --x;
            ans += it->second;
            x->second += it->second;
        }
        return ans;
    }
};

class Solution {
    public int[] dailyTemperatures(int[] temperatures) {

        int[] arr = new int[temperatures.length]; 

        ArrayDeque<int[]> st = new ArrayDeque<>(); 

        int i=0, j=temperatures.length ; 

        while( j >= 0){

            if(st.size()==0){
                arr[i++] = 0;
            }
            else  if (st.size()>0 && st.peek()[0]>temperatures[j])
            {
                arr[i++]=1;
            } 
            else if (st.size()>0 && st.peek()[0]<=temperatures[j])

            {  while(st.size()>0 && st.peek()[0]<=temperatures[j]){
                    st.pop();

                }
            if(st.size()==0){
                arr[i++]=0;
            }
            else{
                arr[i++]=st.peek()[1]-j;
            }
            } 
        st.push(new int[]{temperatures[j],j}); 
        j--;
        }


        int[] ans = new int[temperatures.length]; int k=0;
        for(i=arr.length-1; i>=0; i--){
            ans[k++]=arr[i];
        }
        return ans;
    }
}
